using System; using System.Collections.Generic; using Ameritrack_Xam.Pages.ViewModels; using Ameritrack_Xam.PCL.Helpers; using System.Threading.Tasks; using Xamarin.Forms; using System.Collections.ObjectModel; using Ameritrack_Xam.PCL.Models;   namespace Ameritrack_Xam.Pages.Views {     public partial class MyInspectionsPage : ContentPage     {         private MyInspectionsVM ViewModel;         private static ObservableCollection<Report> InspectionList = new ObservableCollection<Report>();          public MyInspectionsPage()         {             InitializeComponent();              ViewModel = new MyInspectionsVM();         }          protected async override void OnAppearing()         {             await GetReport();              base.OnAppearing();         }          private async Task GetReport() {             InspectionList.Clear();             var reports = await ViewModel.GetReportsByEmployee(UserDataCache.CurrentEmployeeData);              foreach (var report in reports) {                 InspectionList.Add(report);             }              listView.ItemsSource = InspectionList;         }     } }